    private void btnconfirmaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnconfirmaActionPerformed
        // TODO add your handling code here:
        String N1 = txtresposta.getText();
        String resposta = "perna";
        
        String respostaMaiuscula = N1.toUpperCase();
        
        if ("pernalonga".equalsIgnoreCase(N1)) {
            resposta = "voce acertou";
        }else if(respostaMaiuscula.equals("XX")){
            resposta = "burro";
        }else if(N1.equalsIgnoreCase("ss")){
            resposta = "besta";
        }
      
        
        txtcertoerrado.setText(resposta);
        
        
        
    }//GEN-LAST:event_btnconfirmaActionPerformed

